INSERT INTO Event (EventName, CityID, EventDescription, EventAddress, EventWebsite, EventStartDateTime, EventEndDateTime, EventTheme, EventImage, EventWheelchairAccess) VALUES ('Green Day Buenos Aires',1,'Green Day, one of the most  popular bands in the world is visiting Buenos Aires. Come and you will have the Time of Your Life!','Estadio Vélez, Av. Juan B. Justo 9200, Capital Federal','http://www.vuenosairez.com/ar/ciudad-de-buenos-aires/agenda/green-day-en-argentina/164501','10/11/17 16:00','11/11/17 01:00','Music','greendayba',0);
INSERT INTO Event (EventName, CityID, EventDescription, EventAddress, EventWebsite, EventStartDateTime, EventEndDateTime, EventTheme, EventImage, EventWheelchairAccess) VALUES ('Earth, Wind & Fire',2,'Earth, Wind & Fire is an American band that has spanned the musical genres of R&B, soul, funk, jazz, disco, pop, rock, Latin and African. They are one of the most successful bands of all time.','4321 West Flamingo Road, Las Vegas, NV','http://www.bandsintown.com/Earth,WindAndFire','14/07/17 19:00','14/07/17 23:00','Music','ewfvegas',1);
INSERT INTO Event (EventName, CityID, EventDescription, EventAddress, EventWebsite, EventStartDateTime, EventEndDateTime, EventTheme, EventImage, EventWheelchairAccess) VALUES ('International Ballet Gala',3,'The highly successful 2009 International Ballet Gala returns with current and future stars from renowned international ballet companies such as Bolshoi Ballet, Kirov Ballet, American Ballet Theatre and special guest artists performing spectacular dance pieces and great virtuosity in world famous grand pas de deux’s of the classical repertoire supported by dancers of the Australian Conservatoire of Ballet.','Arts Centre Melbourne, 100 Saint Kilda Road, Melbourne VIC','https://www.artscentremelbourne.com.au/whats-on/2017/ballet/international-ballet-gala','15/07/17 19:30','15/07/17 23:00','Ballet','intballetgalamelbourne',1);
INSERT INTO Event (EventName, CityID, EventDescription, EventAddress, EventWebsite, EventStartDateTime, EventEndDateTime, EventTheme, EventImage, EventWheelchairAccess) VALUES ('Rodrigo y Gabriela @ National Concert Hall',4,'Rodrigo y Gabriela celebrate the tenth anniversary re-release of their classic self-titled break-out album with a special performance at the National Concert Hall. Originally released in 2006, the album topped the charts and helped establish the Mexican guitar duo as one of the most popular acoustic instrumental artists in the world.','The National Concert Hall, 2 Earlsfort Terrace, Dublin 2','https://www.nch.ie/Online/Rodrigo-Y-Gabriela-15Jul17','15/07/17 20:00','15/07/17 23:30','Music','rodgabdub',1);
INSERT INTO Tour (TourName, CityID, TourDescription, TourAddress, TourWebsite, TourOperator, TourPrice, TourRating, TourOperatingTimes, TourImage, TourPhone, TourWheelchairAccess) VALUES ('BuenosTours - Buenos Aires Private Walking Tours',1,'Discover the real Buenos Aires up close and personal on a private walking tour. If you have a whole day available, our Full Day Walking Tour is the best way to see the most important sights of the city. Less time? Try a Half Day Tour of the Historical City Center, a concise intro to the city’s fascinating history & culture. ','San Telmo, Capital Federal, Buenos Aires','http://www.buenostours.com/','BuenosTours',1.9,5,'Mon-Sun 08:30-19:30','buenostoura','+54-11-5325-7020',0);
INSERT INTO Tour (TourName, CityID, TourDescription, TourAddress, TourWebsite, TourOperator, TourPrice, TourRating, TourOperatingTimes, TourImage, TourPhone, TourWheelchairAccess) VALUES ('Las Vegas Walking Tours',2,'We know you’re looking for things to do in Las Vegas, why not consider a walking tour! Las Vegas Walking Tours are fun, affordable, informative and quite possibly the best travel activity. Walking tours accommodate families, couples and even the single adventurous traveler while offering the opportunity to meet other travelers from around the world and possibly even make new friends.','Las Vegas, NV 89101, Nevada','https://lasvegaswalkingtours.net/','Las Vegas Walking Tours',3.1,4.3,'Mon-Sat 11:00-16:30\r\nTue,Wed 19:00-21:00\r\nSun14:00-21:00','vegaswalkingtours','+1-702-960-4421',1);
INSERT INTO Tour (TourName, CityID, TourDescription, TourAddress, TourWebsite, TourOperator, TourPrice, TourRating, TourOperatingTimes, TourImage, TourPhone, TourWheelchairAccess) VALUES ('Bunyip Tours Melbourne',3,'From the spectacular Great Ocean Road to the world famous little penguins on Phillip Island, from the pristine beaches of the southernmost region of mainland Australia, Wilsons Promontory to the charming Mornington Peninsula, Bunyip Tours features so many unique places to visit and things to see and do. ','570 Flinders St, Melbourne VIC 3000, Australia','http://www.bunyiptours.com/','Bunyip Tours Melbourne',3.9,4.4,'Mon-Sun 07:00-21:00','bunyiptours','+61-3-9650-9680',1);
INSERT INTO Tour (TourName, CityID, TourDescription, TourAddress, TourWebsite, TourOperator, TourPrice, TourRating, TourOperatingTimes, TourImage, TourPhone, TourWheelchairAccess) VALUES ('Viking Splash Tours',4,'Established in 1999, Viking Splash Tours is one of Ireland’s premier tour companies. Combining authentic World War II amphibious vehicles – the DUKWs – with an imaginative and exciting Viking-themed tour, the Viking Splash experience is unrivalled in Dublin City.','St Stephens Green North, Dublin 2, Dublin','http://www.vikingsplash.com/','Viking Splash Tours',2.2,4.3,'Mon-Sun 09:00-17:00','vikingsplash','+353-1-707-6000',0);
INSERT INTO Attraction (AttractionName, CityID, AttractionDescription, AttractionAddress, AttractionNeighbourhood, AttractionWebsite,AttractionPrice, AttractionRating, AttractionOpeningHours, AttractionImage, AttractionPhone, AttractionWheelchairAccess) VALUES ('Buenos Aires Ecological Reserve',1,'Buenos Aires Ecological Reserve, Reserva Ecológica de Buenos Aires, also known as Costanera Sur Ecological Reserve, Reserva Ecológica Costanera Sur, is a 865-acre (3.50 km2) tract of low land on the Río de la Plata riverbank located on the east side of the district of Puerto Madero in Buenos Aires. Being adjacent to the city, a visit to the Ecological Reserve is an escape from the hustle and bustle of the metropolis. A number of trails leading to the river are perfect for birdwatching, strolling, cycling and nature lovers.','Achaval Rodriguez T., Dr. Av. 1550, Costanera Sur','Consanera Sur','http://www.buenosaires.gob.ar/ambienteyespaciopublico/mantenimiento/espaciosverdes/reservaecologica',0,4.6,'Tue-Sun 08:00-18:00','ecoreserve','+54-11-4893-1853',1);
INSERT INTO Attraction (AttractionName, CityID, AttractionDescription, AttractionAddress, AttractionNeighbourhood, AttractionWebsite,AttractionPrice, AttractionRating, AttractionOpeningHours, AttractionImage, AttractionPhone, AttractionWheelchairAccess) VALUES ('Buenos Aires Japanese Gardens',1,'The Buenos Aires Japanese Gardens are a public space administered by the non-profit Japanese Argentine Cultural Foundation in Buenos Aires, Argentina, and are one of the largest Japanese gardens of its type in the world outside Japan.','Av. Casares 2966, 1425 Palermo, Distrito Federal','Palermo','http://www.jardinjapones.org.ar/',2.1,4.5,'Mon-Sun 10:00-18:00','japanesegardensba','+54-11-4804-4922',1);
INSERT INTO Attraction (AttractionName, CityID, AttractionDescription, AttractionAddress, AttractionNeighbourhood, AttractionWebsite,AttractionPrice, AttractionRating, AttractionOpeningHours, AttractionImage, AttractionPhone, AttractionWheelchairAccess) VALUES ('St Patrick''s Cathedral',4,'Saint Patrick''s Cathedral in Dublin, Ireland, founded in 1191, is the National Cathedral of the Church of Ireland. With its 43-metre spire, St. Patrick''s is the tallest church in Ireland and the largest.','St Patrick''s Close, Wood Quay, Dublin 8','Dublin 8','https://www.stpatrickscathedral.ie/',2.9,4.1,'Mon-Sun 09:00-18:00','stpatscathedral','+353-1-453-9472',1);
INSERT INTO Attraction (AttractionName, CityID, AttractionDescription, AttractionAddress, AttractionNeighbourhood, AttractionWebsite,AttractionPrice, AttractionRating, AttractionOpeningHours, AttractionImage, AttractionPhone, AttractionWheelchairAccess) VALUES ('Sea Life Melbourne Aquarium',3,'Situated on the riverside, just opposite the Crown Casino, Melbourne Aquarium is home to thousands of animals, including sharks, turtles, stingrays, sawfish, tropical fish and much, much more.','King St & Flinders St, Melbourne VIC 3000, Australia','South Wharf','https://www.melbourneaquarium.com.au/',2.5,4.1,'Mon-Sun 09:30-18:00','sealifemelbourne','+61-1800-026-576',1);
